mapper.select:
SELECT post_image_url
FROM post_image
WHERE post_id=#{id} AND is_deleted=0;

select count(*)
from post_like
where post_id=#{id} and is_deleted=0;

SELECT count(*)
FROM comment
Where post_id=#{id} and is_deleted=0;

SELECT count(*)
FROM post_share
Where post_id=#{id} and is_deleted=0;

mapper.update:
UPDATE post
SET is_deleted=1
WHERE id=#{postId};

UPDATE post_image
SET is_deleted=1
WHERE post_id=#{postId} AND is_deleted=0;

UPDATE post
SET content=#{post.content}
WHERE id=#{postId} and is_deleted=0;
