mapper.update:
UPDATE user
SET is_deleted = 1
WHERE id = #{id} AND is_deleted = 0;

mapper.select:
SELECT id
FROM user
WHERE id = #{id}

choose.otherwise:
SELECT id, name, profession, image_url as imageUrl
FROM user
WHERE id not in (
    SELECT followed_user_id
    FROM follow
    WHERE user_id = #{userId}
    )
AND id != #{userId}
ORDER BY rand()
LIMIT 7;

mapper.select:
SELECT id, name, profession, image_url as imageUrl
FROM user
WHERE name like concat('%', #{keyword}, '%');

choose.when:
SELECT id, name, nickname, profession, image_url as imageUrl
FROM user
WHERE id not in (
SELECT followed_user_id
FROM follow
WHERE user_id=#{userId}
group by followed_user_id
)
AND id != #{userId}
ORDER BY rand()
LIMIT 9;
