mapper.update:
UPDATE user
SET is_deleted = 1
WHERE id = #{id} AND is_deleted = 0;

mapper.select:
SELECT id
FROM user
WHERE id = #{id}

SELECT id, name, profession, image_url as imageUrl
FROM user
LIMIT 7;

SELECT account, name, profession, image_url as imageUrl
FROM user
WHERE name like concat('%', #{keyword}, '%');
